package Casting;

public class PrimitiveCasting5 {
	
	void Shiv( double a) {
		System.out.println("a value="+a);
	}
	void Test( int b) {
		System.out.println("b value="+b);
	}
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		PrimitiveCasting5 S1=new PrimitiveCasting5();
		S1.Shiv(10);
		S1.Test(10);
		
		

	}

}
